package org.seckill.dao;import org.apache.ibatis.annotations.Param;import org.seckill.entity.Seckill;import java.util.Date;import java.util.List;/** * Created by saplmm on 2016/10/12. */public interface SeckillDao {    /**     * 减库存     *     * @param seckillId     * @param killTime     * @return     */    int reduceNumber(@Param("seckillId") long seckillId, @Param("killTime") Date killTime);    /**     * @param seckillId     * @return     */    Seckill queryById(long seckillId);    /**     * 查询所有的秒杀     * 注意 @Param 的作用，保证sql模版能够找到这个参数...     *     * @param offest     * @param limit     * @return     */    List<Seckill> queryAll(@Param("offset") int offest, @Param("limit") int limit);}