package org.seckill.service.impl;import org.seckill.dao.SeckillDao;import org.seckill.entity.Seckill;import org.seckill.service.HelloService;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * Created by saplmm on 2016/10/13. */@Servicepublic class HelloServiceImpl implements HelloService {    private final Logger logger = LoggerFactory.getLogger(this.getClass());    @Autowired    private SeckillDao seckillDao;    public void hello() {        System.out.println("进入到类hello...");        logger.debug("logger--debug...");        List<Seckill> ss = seckillDao.queryAll(0, 4);        for (Seckill s : ss) {            System.out.println(s);        }    }}